let letters = /^[a-zA-Z+" "]+$/;
let digits = /^[0-9]+$/;

const nameValidation = (Name) => {
    if (Name === "") {
        return false;
    } else if (Name.match(letters)) {
        return true;
    } else {
        return false;
    }
}

const emailValidation = (Email) => {
    let dot = Email.lastIndexOf('.');
    if (Email === "") {
        return false;
    }
}

export const validation = (Name, Email, Phone, Message) => {
    const nameStatus = nameValidation(Name);
    const emailStatus = emailValidation(Email);
}
