let letters = /^[a-zA-Z+" "]+$/;
let digits = /^[0-9]+$/;

const nameValidation = (Name) => {
    if (Name === "") {
        return false;
    } else if (Name.match(letters)) {
        return true;
    } else {
        return false;
    }
}

const emailValidation = (Email) => {
    let dot = Email.lastIndexOf('.');
    let atSymbol = Email.indexOf('@');
    if (Email === "") {
        return false;
    } else if ((dot === -1 && atSymbol === -1) || (dot === -1 || atSymbol === -1)) {
        return false;
    } else if (atSymbol !== Email.lastIndexOf('@')) {
        return false;
    } else if (dot - atSymbol <= 3) {
        return false
    } else if (atSymbol < 3) {
        return false;
    } else {
        return true
    }
}

export const validation = (Name, Email, Phone, Message) => {
    const nameStatus = nameValidation(Name);
    const emailStatus = emailValidation(Email);
    console.log(nameStatus, emailStatus);
}
